import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardContent, CardMedia, Typography, Button, Box } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const ProductCard = ({ product, isAdmin, onDeleteClick }) => {
    const { id, name, price, description, imageUrl, status } = product;

    return (
        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <CardMedia
                component="img"
                height="200"
                image={imageUrl || 'https://via.placeholder.com/200'}
                alt={name}
            />
            <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h6" component="h2">
                    {name}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    {description?.slice(0, 100)}...
                </Typography>
                <Typography variant="h6" color="primary">
                    ${price?.toFixed(2)}
                </Typography>
                <Typography variant="body2" color={status === 'Available' ? 'success.main' : 'error.main'}>
                    {status}
                </Typography>
            </CardContent>
            <Box sx={{ p: 2, display: 'flex', justifyContent: 'space-between' }}>
                <Button 
                    component={Link} 
                    to={`/product/${id}`} 
                    variant="contained" 
                    color="primary"
                >
                    View Details
                </Button>
                {isAdmin && (
                    <Button
                        variant="contained"
                        color="error"
                        startIcon={<DeleteIcon />}
                        onClick={onDeleteClick}
                    >
                        Delete
                    </Button>
                )}
            </Box>
        </Card>
    );
};

export default ProductCard;
