import React, { createContext, useState, useContext, useEffect } from 'react';
import { getUserCart, addToCart as apiAddToCart, removeFromCart as apiRemoveFromCart, updateCartQuantity as apiUpdateQuantity } from '../services/api';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cart, setCart] = useState([]);
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        const loadCart = async () => {
            if (user) {
                try {
                    const cartData = await getUserCart(user.id);
                    setCart(cartData);
                } catch (error) {
                    console.error('Failed to load cart:', error);
                }
            }
        };
        loadCart();
    }, [user]);

    const addToCart = async (product) => {
        if (!user) {
            alert('Please login to add items to cart');
            return;
        }

        try {
            await apiAddToCart(user.id, product.id);
            const updatedCart = await getUserCart(user.id);
            setCart(updatedCart);
        } catch (error) {
            console.error('Failed to add to cart:', error);
        }
    };

    const removeFromCart = async (cartId) => {
        try {
            await apiRemoveFromCart(cartId);
            const updatedCart = await getUserCart(user.id);
            setCart(updatedCart);
        } catch (error) {
            console.error('Failed to remove from cart:', error);
        }
    };

    const updateQuantity = async (cartId, quantity) => {
        try {
            await apiUpdateQuantity(cartId, quantity);
            const updatedCart = await getUserCart(user.id);
            setCart(updatedCart);
        } catch (error) {
            console.error('Failed to update quantity:', error);
        }
    };

    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, updateQuantity }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
