import React, { useState, useEffect } from 'react';
import { getInventoryTransactions, createInventoryTransaction, getProducts } from '../services/api';
import { useNavigate } from 'react-router-dom';

const Inventory = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState({
        type: '',
        startDate: '',
        endDate: ''
    });
    const [products, setProducts] = useState([]);
    const [newTransaction, setNewTransaction] = useState({
        productId: '',
        transactionType: 'In',
        quantity: 1,
        unitPrice: 0,
        notes: ''
    });

    useEffect(() => {
        fetchTransactions();
    }, [filter]);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchTransactions = async () => {
        try {
            setLoading(true);
            const data = await getInventoryTransactions(filter);
            setTransactions(data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchProducts = async () => {
        try {
            const data = await getProducts();
            setProducts(data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const user = JSON.parse(localStorage.getItem('user'));
            const transaction = {
                ...newTransaction,
                productId: parseInt(newTransaction.productId),
                quantity: parseInt(newTransaction.quantity),
                unitPrice: parseFloat(newTransaction.unitPrice),
                createdById: user.id
            };
            await createInventoryTransaction(transaction);
            alert('Transaction created successfully');
            fetchTransactions();
            // Reset form
            setNewTransaction({
                productId: '',
                transactionType: 'In',
                quantity: 1,
                unitPrice: 0,
                notes: ''
            });
        } catch (error) {
            console.error('Error creating transaction:', error);
            alert(error.response?.data || 'Failed to create transaction');
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Inventory Management</h1>
            
            {/* Add Transaction Form */}
            <div className="mb-8 p-4 border rounded">
                <h2 className="text-xl font-semibold mb-4">New Transaction</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                        <select
                            value={newTransaction.productId}
                            onChange={e => setNewTransaction({...newTransaction, productId: e.target.value})}
                            className="border p-2 rounded"
                            required
                        >
                            <option value="">Select Product</option>
                            {products.map(product => (
                                <option key={product.id} value={product.id}>
                                    {product.name}
                                </option>
                            ))}
                        </select>

                        <select
                            value={newTransaction.transactionType}
                            onChange={e => setNewTransaction({...newTransaction, transactionType: e.target.value})}
                            className="border p-2 rounded"
                            required
                        >
                            <option value="In">In</option>
                            <option value="Out">Out</option>
                        </select>

                        <input
                            type="number"
                            min="1"
                            value={newTransaction.quantity}
                            onChange={e => setNewTransaction({...newTransaction, quantity: e.target.value})}
                            placeholder="Quantity"
                            className="border p-2 rounded"
                            required
                        />

                        <input
                            type="number"
                            step="0.01"
                            min="0"
                            value={newTransaction.unitPrice}
                            onChange={e => setNewTransaction({...newTransaction, unitPrice: e.target.value})}
                            placeholder="Unit Price"
                            className="border p-2 rounded"
                            required
                        />

                        <input
                            type="text"
                            value={newTransaction.notes}
                            onChange={e => setNewTransaction({...newTransaction, notes: e.target.value})}
                            placeholder="Notes"
                            className="border p-2 rounded col-span-2"
                        />
                    </div>

                    <button 
                        type="submit"
                        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                    >
                        Create Transaction
                    </button>
                </form>
            </div>

            {/* Filters */}
            <div className="mb-4 flex gap-4">
                <select
                    value={filter.type}
                    onChange={e => setFilter({...filter, type: e.target.value})}
                    className="border p-2 rounded"
                >
                    <option value="">All Types</option>
                    <option value="In">In</option>
                    <option value="Out">Out</option>
                </select>

                <input
                    type="date"
                    value={filter.startDate}
                    onChange={e => setFilter({...filter, startDate: e.target.value})}
                    className="border p-2 rounded"
                />

                <input
                    type="date"
                    value={filter.endDate}
                    onChange={e => setFilter({...filter, endDate: e.target.value})}
                    className="border p-2 rounded"
                />
            </div>

            {/* Transactions Table */}
            <div className="overflow-x-auto">
                <table className="min-w-full bg-white">
                    <thead>
                        <tr>
                            <th className="px-4 py-2">Date</th>
                            <th className="px-4 py-2">Product</th>
                            <th className="px-4 py-2">Type</th>
                            <th className="px-4 py-2">Quantity</th>
                            <th className="px-4 py-2">Unit Price</th>
                            <th className="px-4 py-2">Total</th>
                            <th className="px-4 py-2">Created By</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map(transaction => (
                            <tr key={transaction.id} className="border-t">
                                <td className="px-4 py-2">
                                    {new Date(transaction.transactionDate).toLocaleDateString()}
                                </td>
                                <td className="px-4 py-2">{transaction.productName}</td>
                                <td className="px-4 py-2">{transaction.transactionType}</td>
                                <td className="px-4 py-2">{transaction.quantity}</td>
                                <td className="px-4 py-2">${transaction.unitPrice.toFixed(2)}</td>
                                <td className="px-4 py-2">
                                    ${(transaction.quantity * transaction.unitPrice).toFixed(2)}
                                </td>
                                <td className="px-4 py-2">
                                    {`${transaction.createdBy.firstName} ${transaction.createdBy.lastName}`}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Inventory;
