import React, { useState, useEffect } from 'react';
import { getProducts, createProduct, updateProduct, deleteProduct } from '../services/api';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);
    const [newProduct, setNewProduct] = useState({
        name: '',
        description: '',
        price: '',
        imageUrl: '',
        category: '',
        stockQuantity: '',
        manufacturer: ''
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [deleteConfirm, setDeleteConfirm] = useState({ show: false, productId: null });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const data = await getProducts();
            setProducts(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleCreate = async (e) => {
        e.preventDefault();
        try {
            await createProduct(newProduct);
            setNewProduct({
                name: '',
                description: '',
                price: '',
                imageUrl: '',
                category: '',
                stockQuantity: '',
                manufacturer: ''
            });
            fetchProducts();
        } catch (err) {
            setError(err.message);
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await updateProduct(editingProduct.id, editingProduct);
            setEditingProduct(null);
            fetchProducts();
        } catch (err) {
            setError(err.message);
        }
    };

    const handleDeleteClick = (product) => {
        setDeleteConfirm({ show: true, productId: product.id, productName: product.name });
    };

    const confirmDelete = async () => {
        if (!deleteConfirm.productId) return;
        try {
            await deleteProduct(deleteConfirm.productId);
            setDeleteConfirm({ show: false, productId: null });
            alert('Product discontinued successfully');
            fetchProducts();
        } catch (err) {
            setError(typeof err === 'string' ? err : 'Failed to delete product');
            alert(typeof err === 'string' ? err : 'Failed to delete product');
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="product-management">
            <h2>Product Management</h2>
            
            {/* Create Form */}
            <div className="create-form">
                <h3>Create New Product</h3>
                <form onSubmit={handleCreate}>
                    {/* Add form fields */}
                </form>
            </div>

            {/* Edit Form */}
            {editingProduct && (
                <div className="edit-form">
                    <h3>Edit Product</h3>
                    <form onSubmit={handleUpdate}>
                        {/* Add form fields */}
                    </form>
                </div>
            )}

            {/* Products List */}
            <div className="products-list">
                <h3>Products</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Stock</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id}>
                                <td>{product.name}</td>
                                <td>{product.category}</td>
                                <td>${product.price}</td>
                                <td>{product.stockQuantity}</td>
                                <td>{product.status}</td>
                                <td>
                                    <button onClick={() => setEditingProduct(product)}>
                                        Edit
                                    </button>
                                    <button 
                                        onClick={() => handleDeleteClick(product)}
                                        className="btn-danger"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Delete Confirmation Dialog */}
            {deleteConfirm.show && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Confirm Delete</h3>
                        <p>Are you sure you want to delete "{deleteConfirm.productName}"?</p>
                        <p>This action cannot be undone.</p>
                        <div className="modal-actions">
                            <button onClick={confirmDelete} className="btn-danger">
                                Delete
                            </button>
                            <button 
                                onClick={() => setDeleteConfirm({ show: false, productId: null })}
                                className="btn-secondary"
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductManagement;
