import React, { useState, useEffect } from 'react';
import { 
    getInventoryTransactions, 
    createInventoryTransaction,
    getProducts,
    getLowStockProducts,
    createProduct
} from '../services/api';
import './Inventory.css';

const Inventory = () => {
    const [activeTab, setActiveTab] = useState('transactions');
    const [transactions, setTransactions] = useState([]);
    const [lowStockProducts, setLowStockProducts] = useState([]);
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [filters, setFilters] = useState({
        type: '',
        startDate: '',
        endDate: ''
    });

    const [newTransaction, setNewTransaction] = useState({
        productId: '',
        type: 'In',
        quantity: '',
        unitPrice: '',
        notes: '',
        createdById: JSON.parse(localStorage.getItem('user'))?.id || ''
    });

    const [newProduct, setNewProduct] = useState({
        name: '',
        description: '',
        price: '',
        imageUrl: '',
        category: '',
        stockQuantity: '',
        manufacturer: '',
        createdById: JSON.parse(localStorage.getItem('user'))?.id || ''
    });

    useEffect(() => {
        fetchData();
    }, [activeTab]);

    const fetchData = async () => {
        try {
            setLoading(true);
            if (activeTab === 'transactions') {
                const data = await getInventoryTransactions(filters);
                setTransactions(data);
            } else if (activeTab === 'lowStock') {
                const data = await getLowStockProducts(10);
                setLowStockProducts(data);
            }
            const productsData = await getProducts();
            setProducts(productsData);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleCreateTransaction = async (e) => {
        e.preventDefault();
        try {
            await createInventoryTransaction(newTransaction);
            fetchData();
            setNewTransaction({
                ...newTransaction,
                productId: '',
                quantity: '',
                unitPrice: '',
                notes: ''
            });
        } catch (err) {
            setError(err.message);
        }
    };

    const handleCreateProduct = async (e) => {
        e.preventDefault();
        try {
            await createProduct(newProduct);
            alert('Product created successfully!');
            setNewProduct({
                name: '',
                description: '',
                price: '',
                imageUrl: '',
                category: '',
                stockQuantity: '',
                manufacturer: '',
                createdById: JSON.parse(localStorage.getItem('user'))?.id || ''
            });
            fetchData();
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="inventory-container">
            <div className="inventory-tabs">
                <button 
                    className={activeTab === 'transactions' ? 'active' : ''}
                    onClick={() => setActiveTab('transactions')}
                >
                    Transactions
                </button>
                <button 
                    className={activeTab === 'lowStock' ? 'active' : ''}
                    onClick={() => setActiveTab('lowStock')}
                >
                    Low Stock
                </button>
                <button 
                    className={activeTab === 'newProduct' ? 'active' : ''}
                    onClick={() => setActiveTab('newProduct')}
                >
                    New Product
                </button>
            </div>

            {activeTab === 'newProduct' && (
                <div className="product-form">
                    <h3>Add New Product</h3>
                    <form onSubmit={handleCreateProduct}>
                        <div className="form-group">
                            <label>Name:</label>
                            <input
                                type="text"
                                value={newProduct.name}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    name: e.target.value
                                })}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Description:</label>
                            <textarea
                                value={newProduct.description}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    description: e.target.value
                                })}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Price:</label>
                            <input
                                type="number"
                                value={newProduct.price}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    price: e.target.value
                                })}
                                required
                                min="0"
                                step="0.01"
                            />
                        </div>
                        <div className="form-group">
                            <label>Image URL:</label>
                            <input
                                type="text"
                                value={newProduct.imageUrl}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    imageUrl: e.target.value
                                })}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Category:</label>
                            <select
                                value={newProduct.category}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    category: e.target.value
                                })}
                                required
                            >
                                <option value="">Select Category</option>
                                <option value="cpu">CPU</option>
                                <option value="gpu">GPU</option>
                                <option value="motherboard">Motherboard</option>
                                <option value="ram">RAM</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <label>Initial Stock:</label>
                            <input
                                type="number"
                                value={newProduct.stockQuantity}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    stockQuantity: e.target.value
                                })}
                                required
                                min="0"
                            />
                        </div>
                        <div className="form-group">
                            <label>Manufacturer:</label>
                            <input
                                type="text"
                                value={newProduct.manufacturer}
                                onChange={(e) => setNewProduct({
                                    ...newProduct,
                                    manufacturer: e.target.value
                                })}
                                required
                            />
                        </div>
                        <button type="submit">Create Product</button>
                    </form>
                </div>
            )}
            
            {activeTab === 'transactions' && (
                <>
                    <div className="transaction-form">
                        <h3>New Transaction</h3>
                        <form onSubmit={handleCreateTransaction}>
                            <div className="form-group">
                                <label>Product:</label>
                                <select
                                    value={newTransaction.productId}
                                    onChange={(e) => setNewTransaction({
                                        ...newTransaction,
                                        productId: e.target.value
                                    })}
                                    required
                                >
                                    <option value="">Select Product</option>
                                    {products.map(product => (
                                        <option key={product.id} value={product.id}>
                                            {product.name}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Type:</label>
                                <select
                                    value={newTransaction.type}
                                    onChange={(e) => setNewTransaction({
                                        ...newTransaction,
                                        type: e.target.value
                                    })}
                                    required
                                >
                                    <option value="In">In</option>
                                    <option value="Out">Out</option>
                                    <option value="Adjustment">Adjustment</option>
                                    <option value="Purchase">Purchase</option>
                                    <option value="Return">Return</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Quantity:</label>
                                <input
                                    type="number"
                                    value={newTransaction.quantity}
                                    onChange={(e) => setNewTransaction({
                                        ...newTransaction,
                                        quantity: e.target.value
                                    })}
                                    required
                                    min="1"
                                />
                            </div>

                            <div className="form-group">
                                <label>Unit Price:</label>
                                <input
                                    type="number"
                                    value={newTransaction.unitPrice}
                                    onChange={(e) => setNewTransaction({
                                        ...newTransaction,
                                        unitPrice: e.target.value
                                    })}
                                    required
                                    min="0"
                                    step="0.01"
                                />
                            </div>

                            <div className="form-group">
                                <label>Notes:</label>
                                <textarea
                                    value={newTransaction.notes}
                                    onChange={(e) => setNewTransaction({
                                        ...newTransaction,
                                        notes: e.target.value
                                    })}
                                />
                            </div>

                            <button type="submit">Create Transaction</button>
                        </form>
                    </div>

                    <div className="transactions-list">
                        <h3>Recent Transactions</h3>
                        <table>
                            <thead>
                                <tr>
                                    <th>Date</th>
                                    <th>Product</th>
                                    <th>Type</th>
                                    <th>Quantity</th>
                                    <th>Unit Price</th>
                                    <th>Total</th>
                                    <th>Created By</th>
                                </tr>
                            </thead>
                            <tbody>
                                {transactions.map(transaction => (
                                    <tr key={transaction.id}>
                                        <td>{new Date(transaction.transactionDate).toLocaleDateString()}</td>
                                        <td>{transaction.productName}</td>
                                        <td>{transaction.transactionType}</td>
                                        <td>{transaction.quantity}</td>
                                        <td>${transaction.unitPrice.toFixed(2)}</td>
                                        <td>${(transaction.quantity * transaction.unitPrice).toFixed(2)}</td>
                                        <td>{`${transaction.createdBy?.firstName} ${transaction.createdBy?.lastName}`}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </>
            )}

            {activeTab === 'lowStock' && (
                <div className="low-stock-list">
                    <h3>Low Stock Products</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Current Stock</th>
                                <th>Status</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {lowStockProducts.map(product => (
                                <tr key={product.id}>
                                    <td>{product.name}</td>
                                    <td>{product.stockQuantity}</td>
                                    <td>{product.status}</td>
                                    <td>
                                        <button onClick={() => {
                                            setActiveTab('transactions');
                                            setNewTransaction({
                                                ...newTransaction,
                                                productId: product.id,
                                                type: 'In'
                                            });
                                        }}>
                                            Restock
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default Inventory;
