import React, { useState, useEffect } from 'react';
import '../styles/EmployeeForm.css';

const EmployeeForm = ({ employee, onSubmit, onClose }) => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phoneNumber: '',
        address: '',
        salary: '',
        roleId: '',
        adminPassword: ''
    });
    const [roles, setRoles] = useState([]);
    const currentUser = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        if (employee) {
            setFormData({
                firstName: employee.firstName,
                lastName: employee.lastName,
                phoneNumber: employee.phoneNumber,
                address: employee.address,
                salary: employee.salary,
                roleId: employee.roleId,
                adminPassword: ''
            });
        }
    }, [employee]);

    useEffect(() => {
        // Fetch roles when component mounts
        const fetchRoles = async () => {
            try {
                const response = await axios.get(`${API_URL}/roles`);
                setRoles(response.data);
            } catch (error) {
                console.error('Error fetching roles:', error);
            }
        };
        fetchRoles();
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Check if role is being elevated to Admin
        const isRoleElevation = employee?.role?.name === 'Manager' && 
            roles.find(r => r.id === parseInt(formData.roleId))?.name === 'Admin';

        if (isRoleElevation && !formData.adminPassword) {
            alert('Admin password required for role elevation');
            return;
        }

        onSubmit({
            id: employee?.id,
            firstName: formData.firstName,
            lastName: formData.lastName,
            phoneNumber: formData.phoneNumber,
            address: formData.address,
            salary: Number(formData.salary),
            roleId: parseInt(formData.roleId),
            userId: employee?.userId
        });
    };

    return (
        <div className="employee-form-overlay">
            <div className="employee-form">
                <h2>{employee ? 'Edit Employee' : 'Add New Employee'}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>First Name:</label>
                        <input
                            type="text"
                            value={formData.firstName}
                            onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Last Name:</label>
                        <input
                            type="text"
                            value={formData.lastName}
                            onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Phone Number:</label>
                        <input
                            type="tel"
                            value={formData.phoneNumber}
                            onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label>Address:</label>
                        <input
                            type="text"
                            value={formData.address}
                            onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label>Salary:</label>
                        <input
                            type="number"
                            value={formData.salary}
                            onChange={(e) => setFormData({ ...formData, salary: e.target.value })}
                            required
                        />
                    </div>
                    
                    {currentUser?.role === 'Admin' && (
                        <div className="form-group">
                            <label>Role:</label>
                            <select
                                value={formData.roleId}
                                onChange={(e) => setFormData({ ...formData, roleId: e.target.value })}
                                required
                            >
                                <option value="">Select Role</option>
                                {roles.map(role => (
                                    <option key={role.id} value={role.id}>
                                        {role.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    )}

                    {formData.roleId && roles.find(r => r.id === parseInt(formData.roleId))?.name === 'Admin' && (
                        <div className="form-group">
                            <label>Admin Password (Required for role elevation):</label>
                            <input
                                type="password"
                                value={formData.adminPassword}
                                onChange={(e) => setFormData({ ...formData, adminPassword: e.target.value })}
                            />
                        </div>
                    )}

                    <div className="form-buttons">
                        <button type="submit" className="btn-save">Save</button>
                        <button type="button" className="btn-cancel" onClick={onClose}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EmployeeForm;
