import React, { useState } from 'react';

const QuickPriceFilter = ({ onPriceRangeSelect }) => {
    const [activeRange, setActiveRange] = useState(null);

    const priceRanges = [
        { id: 'nofilter', label: 'No Filter', min: null, max: null },
        { id: 'under100', label: 'Under $100', min: 0, max: 100 },
        { id: '100to500', label: '$100 - $500', min: 100, max: 500 },
        { id: '500to1000', label: '$500 - $1000', min: 500, max: 1000 },
        { id: 'over1000', label: 'Over $1000', min: 1000, max: null }
    ];

    const handleRangeSelect = (range) => {
        setActiveRange(range.id);
        onPriceRangeSelect(range);
    };

    return (
        <div className="card">
            <h3 style={{ marginBottom: '1rem' }}>Price Range</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                {priceRanges.map((range) => (
                    <button
                        key={range.id}
                        onClick={() => handleRangeSelect(range)}
                        style={{
                            textAlign: 'left',
                            padding: '0.75rem',
                            backgroundColor: activeRange === range.id ? 'var(--primary-color)' : 'white',
                            color: activeRange === range.id ? 'white' : 'var(--text-color)',
                            border: '1px solid var(--primary-color)',
                            borderRadius: '0.375rem',
                            cursor: 'pointer',
                            transition: 'all 0.2s ease'
                        }}
                    >
                        {range.label}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default QuickPriceFilter;
