import React from 'react';
import { useCart } from '../context/CartContext';

const Cart = () => {
    const { cart, removeFromCart, updateQuantity } = useCart();

    const handleRemoveFromCart = async (cartProductId) => {
        try {
            await removeFromCart(cartProductId);
        } catch (error) {
            console.error('Error removing item:', error);
            alert('Failed to remove item from cart');
        }
    };

    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    if (!cart || cart.length === 0) {
        return <div className="container">Your cart is empty</div>;
    }

    return (
        <div className="container">
            <h1>Shopping Cart</h1>
            <div className="cart-items">
                {cart.map(item => (
                    <div key={item.cartProductId} className="card cart-item">
                        <img src={item.product.imageUrl} alt={item.product.name} style={{ width: '100px' }} />
                        <div>
                            <h3>{item.product.name}</h3>
                            <p>${item.product.price.toFixed(2)}</p>
                            <input
                                type="number"
                                min="1"
                                value={item.quantity}
                                onChange={(e) => updateQuantity(item.cartProductId, parseInt(e.target.value))}
                            />
                            <button onClick={() => handleRemoveFromCart(item.cartProductId)}>Remove</button>
                        </div>
                    </div>
                ))}
            </div>
            <div className="cart-total">
                <h2>Total: ${total.toFixed(2)}</h2>
            </div>
        </div>
    );
};

export default Cart;
