import React, { useState, useEffect } from 'react';
import { getProducts, createProduct, updateProduct, deleteProduct } from '../services/api';

const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [editingProduct, setEditingProduct] = useState(null);
    const [newProduct, setNewProduct] = useState({
        name: '',
        description: '',
        price: '',
        imageUrl: '',
        category: '',
        stockQuantity: '',
        manufacturer: ''
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // ThÃªm state cho validation
    const [errors, setErrors] = useState({});
    const [categories] = useState(['cpu', 'gpu', 'motherboard', 'ram', 'storage', 'psu', 'case']);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const data = await getProducts();
            setProducts(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const validateForm = (formData) => {
        const errors = {};
        
        if (!formData.name?.trim()) errors.name = 'Name is required';
        else if (formData.name.length < 3) errors.name = 'Name must be at least 3 characters';
        
        if (!formData.description?.trim()) errors.description = 'Description is required';
        else if (formData.description.length > 500) errors.description = 'Description is too long';
        
        if (!formData.price) errors.price = 'Price is required';
        else if (formData.price <= 0) errors.price = 'Price must be greater than 0';
        
        if (!formData.imageUrl?.trim()) errors.imageUrl = 'Image URL is required';
        else if (!formData.imageUrl.match(/^https?:\/\/.+/)) errors.imageUrl = 'Invalid URL format';
        
        if (!formData.category) errors.category = 'Category is required';
        
        if (!formData.stockQuantity) errors.stockQuantity = 'Stock quantity is required';
        else if (formData.stockQuantity < 0) errors.stockQuantity = 'Stock quantity cannot be negative';
        
        if (!formData.manufacturer?.trim()) errors.manufacturer = 'Manufacturer is required';
        
        return errors;
    };

    const handleInputChange = (e, form) => {
        const { name, value } = e.target;
        if (form === 'new') {
            setNewProduct(prev => ({
                ...prev,
                [name]: value
            }));
        } else {
            setEditingProduct(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleCreate = async (e) => {
        e.preventDefault();
        const validationErrors = validateForm(newProduct);
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }
        
        try {
            await createProduct(newProduct);
            setNewProduct({
                name: '',
                description: '',
                price: '',
                imageUrl: '',
                category: '',
                stockQuantity: '',
                manufacturer: ''
            });
            setErrors({});
            fetchProducts();
            alert('Product created successfully!');
        } catch (err) {
            setError(err.message);
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await updateProduct(editingProduct.id, editingProduct);
            setEditingProduct(null);
            fetchProducts();
        } catch (err) {
            setError(err.message);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                await deleteProduct(id);
                fetchProducts();
            } catch (err) {
                setError(err.message);
            }
        }
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="product-management">
            <h2>Product Management</h2>
            
            {/* Create Form */}
            <div className="create-form">
                <h3>Create New Product</h3>
                <form onSubmit={handleCreate}>
                    <div className="form-group">
                        <label>Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={newProduct.name}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.name ? 'error' : ''}
                        />
                        {errors.name && <span className="error-message">{errors.name}</span>}
                    </div>

                    <div className="form-group">
                        <label>Description:</label>
                        <textarea
                            name="description"
                            value={newProduct.description}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.description ? 'error' : ''}
                        />
                        {errors.description && <span className="error-message">{errors.description}</span>}
                    </div>

                    <div className="form-group">
                        <label>Price:</label>
                        <input
                            type="number"
                            name="price"
                            step="0.01"
                            value={newProduct.price}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.price ? 'error' : ''}
                        />
                        {errors.price && <span className="error-message">{errors.price}</span>}
                    </div>

                    <div className="form-group">
                        <label>Image URL:</label>
                        <input
                            type="url"
                            name="imageUrl"
                            value={newProduct.imageUrl}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.imageUrl ? 'error' : ''}
                        />
                        {errors.imageUrl && <span className="error-message">{errors.imageUrl}</span>}
                    </div>

                    <div className="form-group">
                        <label>Category:</label>
                        <select
                            name="category"
                            value={newProduct.category}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.category ? 'error' : ''}
                        >
                            <option value="">Select a category</option>
                            {categories.map(cat => (
                                <option key={cat} value={cat}>{cat.toUpperCase()}</option>
                            ))}
                        </select>
                        {errors.category && <span className="error-message">{errors.category}</span>}
                    </div>

                    <div className="form-group">
                        <label>Stock Quantity:</label>
                        <input
                            type="number"
                            name="stockQuantity"
                            value={newProduct.stockQuantity}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.stockQuantity ? 'error' : ''}
                        />
                        {errors.stockQuantity && <span className="error-message">{errors.stockQuantity}</span>}
                    </div>

                    <div className="form-group">
                        <label>Manufacturer:</label>
                        <input
                            type="text"
                            name="manufacturer"
                            value={newProduct.manufacturer}
                            onChange={(e) => handleInputChange(e, 'new')}
                            className={errors.manufacturer ? 'error' : ''}
                        />
                        {errors.manufacturer && <span className="error-message">{errors.manufacturer}</span>}
                    </div>

                    <button type="submit">Create Product</button>
                </form>
            </div>

            {/* Edit Form */}
            {editingProduct && (
                <div className="edit-form">
                    <h3>Edit Product</h3>
                    <form onSubmit={handleUpdate}>
                        {/* Add form fields */}
                    </form>
                </div>
            )}

            {/* Products List */}
            <div className="products-list">
                <h3>Products</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Stock</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id}>
                                <td>{product.name}</td>
                                <td>{product.category}</td>
                                <td>${product.price}</td>
                                <td>{product.stockQuantity}</td>
                                <td>{product.status}</td>
                                <td>
                                    <button onClick={() => setEditingProduct(product)}>
                                        Edit
                                    </button>
                                    <button onClick={() => handleDelete(product.id)}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ProductManagement;
