import React from 'react';
import { useCart } from '../context/CartContext';
import { useNavigate, Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
    const { addToCart } = useCart();
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user'));

    const handleAddToCart = async (e) => {
        e.preventDefault(); // Prevent navigation when clicking the button
        if (!user) {
            alert('Please login to add items to cart');
            navigate('/login');
            return;
        }
        try {
            const result = await addToCart(product);
            console.log('Added to cart:', result);
            alert('Product added to cart successfully!');
        } catch (error) {
            console.error('Error adding to cart:', error);
            alert(error.response?.data || 'Failed to add product to cart');
        }
    };

    const calculateSalePrice = () => {
        if (product.sale && 
            new Date(product.sale.startDate) <= new Date() && 
            new Date(product.sale.endDate) >= new Date()) {
            return product.price * (1 - product.sale.discountPercent / 100);
        }
        return product.price;
    };

    const isOnSale = product.sale && 
        new Date(product.sale.startDate) <= new Date() && 
        new Date(product.sale.endDate) >= new Date();

    return (
        <Link to={`/product/${product.id}`} className="card product-card">
            {isOnSale && (
                <div className="sale-badge">-{product.sale.discountPercent}%</div>
            )}
            <img src={product.imageUrl} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <div className="price-container">
                {isOnSale ? (
                    <>
                        <span className="original-price">${product.price.toFixed(2)}</span>
                        <span className="sale-price">${calculateSalePrice().toFixed(2)}</span>
                    </>
                ) : (
                    <span className="regular-price">${product.price.toFixed(2)}</span>
                )}
            </div>
            <p>Stock: {product.stockQuantity}</p>
            <button onClick={handleAddToCart}>Add to Cart</button>
        </Link>
    );
};

export default ProductCard;
