import React from 'react';
import { Navigate } from 'react-router-dom';

const ProtectedRoute = ({ children, allowedRoles }) => {
    const user = JSON.parse(localStorage.getItem('user'));
    console.log('Protected Route - User:', user); // Debug log
    console.log('Protected Route - Allowed Roles:', allowedRoles); // Debug log

    if (!user) {
        console.log('No user found, redirecting to login'); // Debug log
        return <Navigate to="/login" />;
    }

    const hasRequiredRole = allowedRoles.some(
        role => user.role.toLowerCase() === role.toLowerCase()
    );
    console.log('Has required role:', hasRequiredRole); // Debug log

    if (!hasRequiredRole) {
        console.log('User does not have required role, redirecting to home'); // Debug log
        return <Navigate to="/" />;
    }

    return children;
};

export default ProtectedRoute;
