import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import QuickPriceFilter from '../components/QuickPriceFilter';

const CategoryProducts = () => {
    const { id } = useParams();
    const [products, setProducts] = useState([]);
    const [category, setCategory] = useState(null);
    const [sortOption, setSortOption] = useState('');
    const [priceRange, setPriceRange] = useState(null);

    useEffect(() => {
        fetchCategoryAndProducts();
    }, [id]);

    const fetchCategoryAndProducts = async () => {
        try {
            const [categoryResponse, productsResponse] = await Promise.all([
                axios.get(`/api/categories/${id}`),
                axios.get(`/api/products/category/${id}`)
            ]);
            setCategory(categoryResponse.data);
            setProducts(productsResponse.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSort = (option) => {
        setSortOption(option);
        let sortedProducts = [...products];
        switch (option) {
            case 'price-asc':
                sortedProducts.sort((a, b) => a.price - b.price);
                break;
            case 'price-desc':
                sortedProducts.sort((a, b) => b.price - a.price);
                break;
            case 'name-asc':
                sortedProducts.sort((a, b) => a.name.localeCompare(b.name));
                break;
            case 'name-desc':
                sortedProducts.sort((a, b) => b.name.localeCompare(a.name));
                break;
            default:
                break;
        }
        setProducts(sortedProducts);
    };

    const handlePriceRange = (range) => {
        setPriceRange(range);
        filterProducts(range);
    };

    const filterProducts = async (range) => {
        try {
            const response = await axios.get(`/api/products/category/${id}`, {
                params: { minPrice: range.min, maxPrice: range.max }
            });
            setProducts(response.data);
        } catch (error) {
            console.error('Error filtering products:', error);
        }
    };

    return (
        <div className="container">
            <div style={{ display: 'flex', gap: '2rem', marginTop: '2rem' }}>
                {/* Left Sidebar */}
                <div style={{ width: '250px' }}>
                    <QuickPriceFilter onPriceRangeSelect={handlePriceRange} />
                </div>

                {/* Main Content */}
                <div style={{ flex: 1 }}>
                    {/* Banner */}
                    <div className="card" style={{ marginBottom: '2rem', height: '200px' }}>
                        <img 
                            src={category?.bannerUrl || '/default-banner.jpg'} 
                            alt="Category Banner"
                            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                        />
                    </div>

                    {/* Category Info and Sorting */}
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                        <div>
                            <h2>{category?.name}</h2>
                            <p>{products.length} Products</p>
                        </div>
                        <select 
                            value={sortOption} 
                            onChange={(e) => handleSort(e.target.value)}
                            className="sort-select"
                        >
                            <option value="">Sort By</option>
                            <option value="price-asc">Price: Low to High</option>
                            <option value="price-desc">Price: High to Low</option>
                            <option value="name-asc">Name: A-Z</option>
                            <option value="name-desc">Name: Z-A</option>
                        </select>
                    </div>

                    {/* Products Grid */}
                    <div className="grid">
                        {products.map((product) => (
                            <div key={product.id} className="card product-card">
                                <img src={product.imageUrl} alt={product.name} />
                                <h3>{product.name}</h3>
                                <p className="price">${product.price}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CategoryProducts;
