import React, { useState, useEffect } from 'react';
import { getInventoryTransactions, createTransaction } from '../services/api';

const Inventory = () => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchTransactions();
    }, []);

    const fetchTransactions = async () => {
        try {
            const data = await getInventoryTransactions();
            setTransactions(data);
        } catch (error) {
            console.error('Error fetching transactions:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container">
            <h1>Inventory Management</h1>
            <div className="transactions-list">
                <h2>Recent Transactions</h2>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Product</th>
                                <th>Type</th>
                                <th>Quantity</th>
                                <th>Unit Price</th>
                                <th>Total</th>
                                <th>Created By</th>
                            </tr>
                        </thead>
                        <tbody>
                            {transactions.map(transaction => (
                                <tr key={transaction.id}>
                                    <td>{new Date(transaction.transactionDate).toLocaleDateString()}</td>
                                    <td>{transaction.productName}</td>
                                    <td>{transaction.transactionType}</td>
                                    <td>{transaction.quantity}</td>
                                    <td>${transaction.unitPrice.toFixed(2)}</td>
                                    <td>${(transaction.quantity * transaction.unitPrice).toFixed(2)}</td>
                                    <td>{`${transaction.createdBy.firstName} ${transaction.createdBy.lastName}`}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default Inventory;
