import React, { useState, useEffect } from 'react';
import '../styles/EmployeeForm.css';

const EmployeeForm = ({ employee, onSubmit, onCancel }) => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phoneNumber: '',
        address: '',
        salary: ''
    });

    useEffect(() => {
        if (employee) {
            setFormData({
                firstName: employee.firstName,
                lastName: employee.lastName,
                phoneNumber: employee.phoneNumber || '',
                address: employee.address || '',
                salary: employee.salary
            });
        }
    }, [employee]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.firstName || !formData.lastName || !formData.salary) {
            alert('Please fill in all required fields');
            return;
        }
        
        // Create a clean update object with only the necessary fields
        const updatedEmployee = {
            id: employee.id,
            userId: employee.userId,
            roleId: employee.roleId,
            firstName: formData.firstName,
            lastName: formData.lastName,
            phoneNumber: formData.phoneNumber || '',
            address: formData.address || '',
            salary: parseFloat(formData.salary),
            hireDate: employee.hireDate // Preserve the original hire date
        };
        
        onSubmit(updatedEmployee);
    };

    return (
        <div className="employee-form-overlay">
            <div className="employee-form-container">
                <h2>Edit Employee</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>First Name *</label>
                        <input
                            type="text"
                            value={formData.firstName}
                            onChange={(e) => setFormData({...formData, firstName: e.target.value})}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Last Name *</label>
                        <input
                            type="text"
                            value={formData.lastName}
                            onChange={(e) => setFormData({...formData, lastName: e.target.value})}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Phone Number</label>
                        <input
                            type="text"
                            value={formData.phoneNumber}
                            onChange={(e) => setFormData({...formData, phoneNumber: e.target.value})}
                        />
                    </div>
                    <div className="form-group">
                        <label>Address</label>
                        <input
                            type="text"
                            value={formData.address}
                            onChange={(e) => setFormData({...formData, address: e.target.value})}
                        />
                    </div>
                    <div className="form-group">
                        <label>Salary *</label>
                        <input
                            type="number"
                            value={formData.salary}
                            onChange={(e) => setFormData({...formData, salary: e.target.value})}
                            required
                            min="0"
                            step="0.01"
                        />
                    </div>
                    <div className="form-actions">
                        <button type="submit" className="btn-save">Save Changes</button>
                        <button type="button" className="btn-cancel" onClick={onCancel}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EmployeeForm;
