import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { getProducts, deleteProduct } from '../services/api';
import ProductCard from '../components/ProductCard';
import DeleteConfirmation from '../components/DeleteConfirmation';
import { Container, Grid, Typography, Box } from '@mui/material';
import '../styles/Products.css';

const Products = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchParams] = useSearchParams();
    const category = searchParams.get('category');
    const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const user = JSON.parse(localStorage.getItem('user'));

    useEffect(() => {
        loadProducts();
    }, [category]);

    const loadProducts = async () => {
        try {
            setLoading(true);
            const data = await getProducts(category);
            setProducts(data);
        } catch (err) {
            setError('Failed to load products');
            console.error('Error:', err);
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <Box sx={{ p: 3 }}><Typography>Loading...</Typography></Box>;
    if (error) return <Box sx={{ p: 3 }}><Typography color="error">{error}</Typography></Box>;

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            <Typography variant="h4" component="h1" gutterBottom>
                {category ? `${category.toUpperCase()} Products` : 'All Products'}
            </Typography>
            <Grid container spacing={3}>
                {products.map(product => (
                    <Grid item xs={12} sm={6} md={4} lg={3} key={product.id}>
                        <ProductCard 
                            product={product}
                            isAdmin={['Admin', 'Manager'].includes(user?.role)}
                            onDeleteClick={() => {
                                setSelectedProduct(product);
                                setDeleteDialogOpen(true);
                            }}
                        />
                    </Grid>
                ))}
            </Grid>
            <DeleteConfirmation
                open={deleteDialogOpen}
                onClose={() => setDeleteDialogOpen(false)}
                onConfirm={async () => {
                    try {
                        await deleteProduct(selectedProduct.id);
                        await loadProducts();
                        setDeleteDialogOpen(false);
                    } catch (err) {
                        setError('Failed to delete product');
                    }
                }}
                itemName={selectedProduct?.name || ''}
            />
        </Container>
    );
};

export default Products;
