import React, { useState, useEffect } from 'react';
import { getEmployees, deleteEmployee, updateEmployee } from '../services/api';
import EmployeeForm from '../components/EmployeeForm';
import '../styles/Employees.css';

const Employees = () => {
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = async () => {
        try {
            const data = await getEmployees();
            setEmployees(data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching employees:', error);
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this employee?')) {
            try {
                await deleteEmployee(id);
                setEmployees(employees.filter(emp => emp.id !== id));
            } catch (error) {
                console.error('Error deleting employee:', error);
            }
        }
    };

    const handleEdit = (employee) => {
        setSelectedEmployee(employee);
        setShowForm(true);
    };

    const handleUpdate = async (formData) => {
        try {
            const updateData = {
                id: selectedEmployee.id,
                firstName: formData.firstName,
                lastName: formData.lastName,
                phoneNumber: formData.phoneNumber,
                address: formData.address,
                salary: parseFloat(formData.salary),
                roleId: selectedEmployee.roleId,
                userId: selectedEmployee.userId
            };

            const response = await updateEmployee(selectedEmployee.id, updateData);
            
            const updatedEmployees = employees.map(emp => 
                emp.id === selectedEmployee.id ? response : emp
            );
            
            setEmployees(updatedEmployees);
            setShowForm(false);
            setSelectedEmployee(null);
        } catch (error) {
            console.error('Error updating employee:', error);
            alert('Failed to update employee: ' + (error.response?.data?.message || 'Unknown error'));
        }
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div className="employee-container">
            <div className="employee-header">
                <h1>Employees Management</h1>
                <button className="btn-edit">Add New Employee</button>
            </div>
            <table className="employee-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Role</th>
                        <th>Phone</th>
                        <th>Hire Date</th>
                        <th>Salary</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(employee => (
                        <tr key={employee.id}>
                            <td>{`${employee.firstName} ${employee.lastName}`}</td>
                            <td>{employee.role.name}</td>
                            <td>{employee.phoneNumber}</td>
                            <td>{new Date(employee.hireDate).toLocaleDateString()}</td>
                            <td>${employee.salary.toFixed(2)}</td>
                            <td className="employee-actions">
                                <button 
                                    className="btn-edit"
                                    onClick={() => handleEdit(employee)}
                                >
                                    Edit
                                </button>
                                <button 
                                    className="btn-delete"
                                    onClick={() => handleDelete(employee.id)}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            
            {showForm && (
                <EmployeeForm
                    employee={selectedEmployee}
                    onSubmit={handleUpdate}
                    onCancel={() => {
                        setShowForm(false);
                        setSelectedEmployee(null);
                    }}
                />
            )}
        </div>
    );
};

export default Employees;
