import React from 'react';

const PriceRangeFilter = ({ onPriceRangeChange }) => {
    const priceRanges = [
        { id: 1, label: 'Under $100', min: 0, max: 100 },
        { id: 2, label: '$100 - $500', min: 100, max: 500 },
        { id: 3, label: '$500 - $1000', min: 500, max: 1000 },
        { id: 4, label: 'Over $1000', min: 1000, max: Infinity }
    ];

    const handleRangeChange = (e) => {
        const selectedRange = priceRanges.find(range => range.id === parseInt(e.target.value));
        onPriceRangeChange(selectedRange);
    };

    return (
        <div className="price-range-filter card">
            <h3>Price Range</h3>
            {priceRanges.map(range => (
                <div key={range.id}>
                    <input
                        type="radio"
                        id={`price-${range.id}`}
                        name="price-range"
                        value={range.id}
                        onChange={handleRangeChange}
                    />
                    <label htmlFor={`price-${range.id}`}>{range.label}</label>
                </div>
            ))}
        </div>
    );
};

export default PriceRangeFilter;
